{% block title %}Edition galery
{% endblock %}
{% block javascripts %}
\t<script src=\"{{ asset('js/delete.js') }}\" defer></script>
{% endblock %}
{% block body %}
\t<p>{{ app.user.email }}</p>
\t<a href=\"{{ path('app_galery') }}\" role=\"button\">Retour Galery</a>

\t<article>
\t\t<h3>{{ galery.name }}</h3>

\t\t<ul>

\t\t\t{% for image in galery.images %}
\t\t\t\t<li>
\t\t\t\t\t<img src=\"{{ asset('/uploads/' ~ image.name) }}\" alt=\"Image\" width=\"150\">
\t\t\t\t\t<a href=\"{{ path('app_galery_del_image', {id: image.id}) }}\" data-delete data-token=\"{{ csrf_token('delete' ~ image.id) }}\">Supprimer</a>
\t\t\t\t</li>

\t\t\t{% endfor %}
\t\t</ul>
\t</article>


\t{{ form_start(form) }}

\t{{ form_row(form.name) }}
\t{{ form_row(form.images) }}
\t<input type=\"submit\" value=\"Enregistrer\">
\t{{ form_end(form) }}

{% endblock %}
